import React, { useState } from'react'

export const AddUser = () => {

    const [user, setUser] = useState({
        name: '',
        email: '',
        password: ''
    })

    const valueInput = e => setUser({...user, [e.target.name]: e.target.value})

    return (
        <div>
            <h1>Cadastrar Usuário</h1>
            <form>
                <label>Nome: </label>
                <input type="text" name='name' placeholder='Nome completo do usuário' onChange={valueInput} /><br/><br/>

                <label>E-mail: </label>
                <input type="email" name='email' placeholder='Melhor e-mail do usuário' /><br/><br/>

                <label>Senha: </label>
                <input type="password" name='password' placeholder='Senha para acessar o sistema' /><br/><br/>

                <button type='submit'>Cadastrar</button>
            </form>
        </div>
    )
}
