import React, { useState } from 'react';
import { Redirect } from 'react-router-dom/cjs/react-router-dom.min';

export const AddUserLogin = () => {

    const [user, setUser] = useState({
        name: '',
        email: '',
        password: ''
    });

    const [status, setStatus] = useState({
        type: '',
        mensagem: ''
    });



    return (
        <div>
            <h1>Cadastrar Usu치rio</h1>

            {status.type === 'error' ? <p style={{ color: "#ff0000" }}>{status.mensagem}</p> : ""}
            {status.type === 'success' ?
                <Redirect to={{
                    pathname: '/',
                    state: {
                        type: "success",
                        mensagem: status.mensagem
                    }
                }} />
                : ""}

            <form>
                <label>Nome*: </label>
                <input type="text" name="name" placeholder="Nome completo do usu치rio" /><br /><br />

                <label>E-mail*: </label>
                <input type="email" name="email" placeholder="Melhor e-mail do usu치rio" /><br /><br />

                <label>Senha*: </label>
                <input type="password" name="password" placeholder="Senha para acessar o sistema" autoComplete="on" /><br /><br />

                * Campo obrigat칩rio<br /><br />

                <button type="submit">Cadastrar</button>
            </form>

        </div>
    );
};
