import React, { useContext } from "react";
import { Context } from '../../Context/AuthContext'

import {Link} from "react-router-dom"

export const Sidebar = () => {

    const { handleLogout } = useContext(Context)

    // const dropdownUserNavbar = async () => {
    //     document.getElementById("dropNavbarUser").classList.toggle("dropdown-menu-action")
    // }

    return (
        <div id className="sidebar">
            <Link to="/dashboard" class="sidebar-nav active"><i class="icon fa-solid fa-gauge-high"></i><span>Dashboard</span></Link>
            <Link to="/users" class="sidebar-nav"><i class="icon fa-solid fa-users"></i><span>Usu√°rio</span></Link>
            <Link to="/view-profile" class="sidebar-nav"><i class="icon fa-solid fa-user"></i><span>Perfil</span></Link>
            <Link onClick={handleLogout} class="sidebar-nav"><i class="icon fa-solid fa-right-from-bracket"></i><span>Sair</span></Link>
        </div>
    )
}
