import React, { useContext } from "react";
import { Context } from '../../Context/AuthContext'

import {Link} from "react-router-dom"

export const Sidebar = () => {

    const { handleLogout } = useContext(Context)

    return (
        <div id="barsSidebar" className="sidebar">
            <Link to="/dashboard" className="sidebar-nav active"><i className="icon fa-solid fa-gauge-high"></i><span>Dashboard</span></Link>
            <Link to="/users" className="sidebar-nav"><i className="icon fa-solid fa-users"></i><span>Usu√°rio</span></Link>
            <Link to="/view-profile" className="sidebar-nav"><i className="icon fa-solid fa-user"></i><span>Perfil</span></Link>
            <Link to="" onClick={handleLogout} className="sidebar-nav"><i className="icon fa-solid fa-right-from-bracket"></i><span>Sair</span></Link>
        </div>
    )
}
