import React from "react";
// import React, { useContext } from "react";
// import { Context } from '../../Context/AuthContext'

export const Sidebar = () => {

    // const { handleLogout } = useContext(Context)

    // const dropdownUserNavbar = async () => {
    //     document.getElementById("dropNavbarUser").classList.toggle("dropdown-menu-action")
    // }

    return (
        <div className="sidebar">
            <a href="index.html" class="sidebar-nav active"><i class="icon fa-solid fa-gauge-high"></i><span>Dashboard</span></a>
            <a href="listar.html" class="sidebar-nav"><i class="icon fa-solid fa-users"></i><span>Listar</span></a>
            <a href="formulario.html" class="sidebar-nav"><i class="icon fa-solid fa-edit"></i><span>Formulário</span></a>
            <a href="visualizar.html" class="sidebar-nav"><i class="icon fa-solid fa-eye"></i><span>Visualizar</span></a>
            <a href="alerta.html" class="sidebar-nav"><i class="icon fa-solid fa-triangle-exclamation"></i><span>Alerta</span></a>
            <a href="button.html" class="sidebar-nav"><i class="icon fa-solid fa-check-square"></i><span>Botões</span></a>
            <a href="login.html" class="sidebar-nav"><i class="icon fa-solid fa-right-from-bracket"></i><span>Sair</span></a>
        </div>
    )
}
