import React, {useEffect, useState} from 'react';

import { servDeleteUser } from '../../services/servDeleteUser';
import api from '../../config/configApi'

import {Link, useLocation} from 'react-router-dom'

export const Users = () => {

    const {state} = useLocation()
    // console.log(state)

    const [data, setData] = useState([])
    const [page, setPage] = useState("")
    const [lastPage, setLastPage] = useState("")

    const [status, setStatus] = useState({
        type: state ? state.type : '',
        mensagem: state ? state.mensagem : ''
    })

    const getUsers = async (page) => {

        if(page === undefined) {
            page = 3
        }
        setPage(page)

        const headers = {
            'headers': {
                'Authorization': "Bearer " + localStorage.getItem('token')
            }
        }

        await api.get("/users/" + page, headers)
        .then((response) => {
            // console.log(response)
            setData(response.data.users)
            setLastPage(response.data.lastPage)
            console.log(response)
        }).catch((err) => {
            if(err.response) {
                setStatus({
                    type: 'error',
                    mensagem: err.response.data.mensagem
                })
                // console.log(err.response.data.mensagem)
            } else {
                setStatus({
                    type: 'error',
                    mensagem: "Erro: Tente mais tarde!"
                })
                // console.log("Erro: Tente mais tarde!")
            }
        })
    }

    useEffect(() => {
        getUsers()
    })

    const deleteUser = async (idUser) => {

        const response = await servDeleteUser(idUser)
        // console.log(response)

        if(response) {
            setStatus({
                type: response.type,
                mensagem: response.mensagem
            })
            getUsers()
        } else {
            setStatus({
                type: "error",
                mensagem: "Erro: Tente mais tarde!"
            })
        }

        /*const headers = {
            'headers': {
                'Authorization': "Bearer " + localStorage.getItem('token')
            }
        }

        await api.delete("/user/" + idUser, headers)
        .then((response) => {
            setStatus({
                type: 'success',
                mensagem: response.data.mensagem
            })
            getUsers()
        }).catch((err) => {
            if(err.response) {
                setStatus({
                    type: 'error',
                    mensagem: err.response.data.mensagem
                })
            } else {
                setStatus({
                    type: 'error',
                    mensagem: "Erro: Tente mais tarde!"
                })
            }
        })*/
    }

    return(
        <>
            <Link to="/dashboard">Dashboard</Link><br/>
            <Link to="/users">Usuários</Link><br/>
            
            <h1>Listar Usuários</h1>

            <Link to="/add-user"><button type='button'>Cadastrar</button></Link><br/>

            {status.type === 'error'? <p style={{color: "#ff0000"}}>{status.mensagem}</p> : ""}
            {status.type === 'success'? <p style={{color: "green"}}>{status.mensagem}</p> : ""}

            <hr/>

            {data.map(user => (
                <div key={user.id}>
                    <span>{user.id}</span><br/>
                    <span>{user.name}</span><br/>
                    <span>{user.email}</span><br/><br/>
                    <Link to={'/view-user/' + user.id}><button type='button'>Visualizar</button></Link>{" "}
                    <Link to={'/edit-user/' + user.id}><button type='button'>Editar</button></Link>{" "}
                    <Link to={"#"}><button type='button' onClick={() => deleteUser(user.id)}>Apagar</button></Link>
                    <hr/>
                </div>
            ))}

            <button type='button'>{page}</button>

            {page !== lastPage ? <button type='button' onClick={() => get}>Última</button> : <button type='button'>Última</button>}

        </>
    );
}
